<template>
  <at-modal v-show="show" class="ms" v-model="dialog" width="400" :maskClosable="false" :showClose="false">
    <div slot="header" style="text-align: center">
      <span>Загрузка проекта</span>
    </div>
    <div class="list">
      <scrollbar>
        <ul class="loaded-list">
          <li
            :class="['loaded-list__item', { 'loaded-list__item--active': selected.label === item.label }]"
            v-for="(item, i) of list"
            :key="`custom_${i}`"
            @click="selected = item"
          >
            <i class="loaded-list__item--icon"></i>
            <span class="loaded-list__item--text">{{ item.label }}</span>
            <div class="loaded-list__item--empty"></div>
            <div class="loaded-list__item--remove" @click.stop="remove(item)">
              <i></i>
            </div>
          </li>
          <li v-if="!list.length" class="loaded-list__no-list">
            Нет проектов для загрузки
          </li>
        </ul>
      </scrollbar>
    </div>
    <template slot="footer">
      <t-button @click="load" :disabled="!selected.label">Загрузить</t-button>
      <t-button @click="dialog = false" cancel>Отменить</t-button>
    </template>
  </at-modal>
</template>

<script>
import { mapGetters } from 'vuex';
export default {
  name: 'ModalLoadModel',
  props: {
    value: Boolean,
    list: Array,
  },
  data: () => ({
    selected: {},
    show: true,
  }),
  computed: {
    ...mapGetters({}),
    dialog: {
      set(value) {
        this.$emit('input', value);
      },
      get() {
        return this.value;
      },
    },
  },
  methods: {
    remove(list) {
      // this.show = false;
      this.$Modal
        .confirm({
          title: 'Внимание!',
          content: `Вы действительно желаете удалить проект «${list.label}» ?`,
          width: 400,
          maskClosable: false,
          showClose: false,
        })
        .then(() => {
          this.$emit('remove', list);
          // this.show = true;
        })
        .catch(() => {
          // this.show = true;
        });
    },
    load() {
      // this.show = false;
      this.$Modal
        .confirm({
          title: 'Внимание!',
          content: `Загрузка проекта удалит текущий. Вы действительно хотите загрузить проект «${this.selected.label}» ?`,
          width: 400,
          maskClosable: false,
          showClose: false,
        })
        .then(() => {
          this.$emit('load', this.selected);
          // this.show = true;
        })
        .catch(() => {
          // this.show = true;
        });
    },
  },
};
</script>

