      .loaded-list__item--icon {
        background-image: url('data:image/svg+xml;base64,');
      }
    }

    &--text {
      line-height: 1;
      padding-left: 10px;
      font-size: 0.875rem;
      user-select: none;
      overflow: hidden;
    }
    &--icon {
      flex: 0 0 20px;
      display: block;
      content: '';
      width: 18px;
      height: 18px;
      background-position: center;
      background-repeat: no-repeat;
      background-size: contain;
      transition: background-image 0.3s ease-in-out;
      background-image: url('data:image/svg+xml;base64,');
    }
    &--empty {
      flex-grow: 1;
    }
    &--remove {
      border-radius: 2px;
      margin-right: 4px;
      padding: 2px;
      i {
        display: block;
        width: 18px;
        height: 18px;
        cursor: pointer;
        user-select: none;

        background-repeat: no-repeat;
        background-position: center;
        transition: background-color 0.3s ease-in-out;
        background-image: url('data:image/svg+xml;base64,');
      }
      &:hover {
        background: rgba(255, 255, 255, 0.2);
      }
    }
  }
}

